// Calculate yesterday's date
const now = new Date();
const yesterday = new Date(now);
yesterday.setDate(yesterday.getDate() - 1);

// Format as YYYY-MM-DD
const year = yesterday.getFullYear();
const month = String(yesterday.getMonth() + 1).padStart(2, '0');
const day = String(yesterday.getDate()).padStart(2, '0');
const dateString = `${year}-${month}-${day}`;

// Build file path for Obsidian daily note
const filePath = `cortex/daily/${dateString}-digest.md`;

return {
  json: {
    date: dateString,
    filePath: filePath
  }
};

const input = $input.first().json;

// Get content from HTTP response (it's the raw markdown text)
const content = typeof input === 'string'
  ? input
  : (input.data || input.body || input.content || '');

// Extract date from markdown header or front matter
let date = 'unknown';

// Pattern: "# Daily Digest - 2025-11-27" or similar
const headerMatch = content.match(/^#\s*Daily Digest[\sâ€”-]+([\d-]+)/m);

// YAML front matter: ---\n date: 2025-11-27\n ... ---
const frontMatterMatch = content.match(/^---[\s\S]+?date:\s*([^\n]+)[\s\S]+?---/);

if (headerMatch) {
  date = headerMatch[1];
} else if (frontMatterMatch) {
  date = frontMatterMatch[1].trim();
}

// Extract Reflection section (if present)
function extractSection(markdown, sectionName) {
  const regex = new RegExp(`##\\s*${sectionName}([\\s\\S]*?)(?=\n##|$)`, 'i');
  const match = markdown.match(regex);
  if (!match) return null;
  return match[1].trim();
}

const reflectionSection = extractSection(content, 'Reflection');

// Extract tasks: any markdown checkbox line in the whole file
const taskRegex = /^\s*-\s*\[.\]\s*.*/gm;
const tasks = content.match(taskRegex) || [];

// Build Slack message
let message = `ðŸ—“ *Daily Digest for ${date}*\n\n`;

if (tasks.length > 0) {
  message += `âœ… *Tasks*\n`;
  for (const task of tasks) {
    message += `${task}\n`;
  }
  message += `\n`;
} else {
  message += `âœ… *Tasks*\nNo tasks found\n\n`;
}

if (reflectionSection) {
  message += `ðŸ§  *Reflection*\n${reflectionSection}`;
} else {
  message += `ðŸ§  *Reflection*\nNo reflection found`;
}

return {
  json: {
    date,
    text: message,
    tasksFound: tasks.length,
    reflectionFound: !!reflectionSection,
  },
};
