// ADR „Éá„Éº„Çø„Çí GitHub Issue Áî®„Å´„Éë„Éº„Çπ
const input = $input.item.json.body || $input.item.json;

// ADR „Éï„Ç°„Ç§„É´„Éë„Çπ„Åã„ÇâÁï™Âè∑„ÇíÊäΩÂá∫
const adrNumber = input.filepath ? input.filepath.match(/ADR-(\d+)/)?.[1] : 'unknown';

// Issue „Çø„Ç§„Éà„É´
const issueTitle = `[ADR-${adrNumber}] ${input.title || 'Untitled ADR'}`;

// Issue Êú¨Êñá
const issueBody = `## ADR Information

**Status**: ${input.status || 'Draft'}
**Date**: ${input.date || new Date().toISOString().split('T')[0]}
**Author**: ${input.author || 'Unknown'}

## Context

${input.context || 'No context provided.'}

## Decision

${input.decision || 'No decision recorded.'}

---

**Source**: [${input.filepath}](https://github.com/dauberside/dauberside.github.io/blob/master/${input.filepath})

ü§ñ Auto-generated by Recipe 04 (n8n workflow)`;

// Issue „É©„Éô„É´
const labels = ['documentation', 'adr'];
if (input.status === 'Proposed') labels.push('needs-review');
if (input.status === 'Accepted') labels.push('accepted');

return {
  json: {
    title: issueTitle,
    body: issueBody,
    labels: labels,
    adrNumber: adrNumber,
    filepath: input.filepath,
    originalData: input
  }
};
