// GitHub Push „Ç§„Éô„É≥„Éà„Åã„Çâ ADR „Éï„Ç°„Ç§„É´„ÇíÊäΩÂá∫
const payload = $input.item.json.body || $input.item.json;
const commits = payload.commits || [];

const adrFiles = [];

for (const commit of commits) {
  const added = commit.added || [];
  const modified = commit.modified || [];

  const allFiles = [...added, ...modified];

  for (const file of allFiles) {
    // docs/decisions/ADR-*.md „ÅÆ„ÅøÂá¶ÁêÜ
    if (file.startsWith('docs/decisions/ADR-') && file.endsWith('.md')) {
      adrFiles.push({
        filepath: file,
        commitSha: commit.id,
        commitMessage: commit.message,
        author: commit.author?.name || commit.author?.username || 'Unknown',
        timestamp: commit.timestamp
      });
    }
  }
}

if (adrFiles.length === 0) {
  // ADR „Éï„Ç°„Ç§„É´„Åå„Å™„ÅÑÂ†¥Âêà„ÅØ„Çπ„Ç≠„ÉÉ„Éó
  return [];
}

// ÂêÑ ADR „Éï„Ç°„Ç§„É´„Åî„Å®„Å´Âá∫Âäõ
return adrFiles.map(file => ({ json: file }));

// Base64 „Éá„Ç≥„Éº„Éâ„Åó„Å¶ Markdown „Éë„Éº„Çπ
const content = Buffer.from($input.item.json.content, 'base64').toString('utf8');
const filepath = $('Filter ADR Files').item.json.filepath;
const author = $('Filter ADR Files').item.json.author;

// ADR Áï™Âè∑„ÇíÊäΩÂá∫
const adrNumber = filepath.match(/ADR-(\d+)/)?.[1] || 'unknown';

// „Çø„Ç§„Éà„É´„ÇíÊäΩÂá∫ÔºàÊúÄÂàù„ÅÆ # Ë°åÔºâ
const titleMatch = content.match(/^#\s+(.+)$/m);
const title = titleMatch ? titleMatch[1] : `ADR-${adrNumber}`;

// Status „ÇíÊäΩÂá∫
const statusMatch = content.match(/^Status:\s*(.+)$/m);
const status = statusMatch ? statusMatch[1].trim() : 'Draft';

// Date „ÇíÊäΩÂá∫
const dateMatch = content.match(/^Date:\s*(.+)$/m);
const date = dateMatch ? dateMatch[1].trim() : new Date().toISOString().split('T')[0];

// Context „Çª„ÇØ„Ç∑„Éß„É≥„ÇíÊäΩÂá∫
const contextMatch = content.match(/##\s+.*Context[\s\S]*?\n\n([\s\S]*?)(?=\n##|$)/i);
const context = contextMatch ? contextMatch[1].trim() : 'No context provided.';

// Decision „Çª„ÇØ„Ç∑„Éß„É≥„ÇíÊäΩÂá∫
const decisionMatch = content.match(/##\s+.*Decision[\s\S]*?\n\n([\s\S]*?)(?=\n##|$)/i);
const decision = decisionMatch ? decisionMatch[1].trim() : 'No decision recorded.';

// Issue „Çø„Ç§„Éà„É´
const issueTitle = `[ADR-${adrNumber}] ${title.replace(/^ADR-\d+:\s*/, '')}`;

// Issue Êú¨Êñá
const issueBody = `## ADR Information

**Status**: ${status}
**Date**: ${date}
**Author**: ${author}

## Context

${context}

## Decision

${decision}

---

**Source**: [${filepath}](https://github.com/dauberside/dauberside.github.io/blob/master/${filepath})

ü§ñ Auto-generated by Recipe 04 Phase 2 (GitHub webhook)`;

// Issue „É©„Éô„É´
const labels = ['documentation', 'adr'];
if (status === 'Proposed') labels.push('needs-review');
if (status === 'Accepted') labels.push('accepted');

return {
  json: {
    title: issueTitle,
    body: issueBody,
    labels: labels,
    adrNumber: adrNumber,
    filepath: filepath,
    status: status,
    author: author
  }
};
