// Calculate yesterday's date in JST (UTC+9)
function getJSTDate(offsetDays = 0) {
  const now = new Date();
  const jstOffset = 9 * 60; // JST = UTC+9
  const jstTime = new Date(now.getTime() + jstOffset * 60 * 1000 + offsetDays * 24 * 60 * 60 * 1000);
  return jstTime.toISOString().split('T')[0]; // YYYY-MM-DD
}

const dateString = getJSTDate(-1); // Yesterday in JST

// Build file path for Obsidian daily note
const filePath = `cortex/daily/${dateString}-digest.md`;

return {
  json: {
    date: dateString,
    filePath: filePath,
    outputPath: `${process.env.WORKSPACE_ROOT}/notifications/daily/${dateString}-digest.md`
  }
};

const input = $input.first().json;
const dateInfo = $('Calculate Yesterday\'s Date (JST)').first().json;

// Get content from HTTP response (it's the raw markdown text)
const content = typeof input === 'string' ? input : (input.data || input.body || input.content || '');

// Extract date from markdown header (e.g., "# Daily Digest 2025-11-19")
let date = dateInfo.date || 'unknown';
const dateMatch = content.match(/# Daily Digest[\sâ€”-]+([\d-]+)/);
if (dateMatch) {
  date = dateMatch[1];
}

// Parse markdown to extract Tasks and Reflection sections
function extractSection(markdown, sectionName) {
  const regex = new RegExp(`##\\s*${sectionName}([\\s\\S]*?)(?=##|$)`, 'i');
  const match = markdown.match(regex);
  if (!match) return null;
  return match[1].trim();
}

function extractTasks(tasksSection) {
  if (!tasksSection) return [];
  const lines = tasksSection.split('\n');
  return lines
    .filter(line => line.match(/^\s*-\s*\[[x \]]/i)) // Match checkbox lines [x] or [ ]
    .map(line => line.trim());
}

// Extract sections
const tasksSection = extractSection(content, 'Tasks');
const reflectionSection = extractSection(content, 'Reflection');

// Parse tasks
const tasks = extractTasks(tasksSection);

// Build Slack message
let slackMessage = `ðŸ—“ *Daily Digest for ${date}*\n\n`;

if (tasks.length > 0) {
  slackMessage += `âœ… *Tasks*\n`;
  tasks.forEach(task => {
    slackMessage += `${task}\n`;
  });
  slackMessage += `\n`;
} else {
  slackMessage += `âœ… *Tasks*\nNo tasks found\n\n`;
}

if (reflectionSection) {
  slackMessage += `ðŸ§  *Reflection*\n${reflectionSection}`;
} else {
  slackMessage += `ðŸ§  *Reflection*\nNo reflection found`;
}

// Build Markdown file content for Claude Code
let markdownContent = `# Daily Digest - ${date}\n\n`;

if (tasks.length > 0) {
  markdownContent += `## Tasks\n`;
  tasks.forEach(task => {
    markdownContent += `${task}\n`;
  });
  markdownContent += `\n`;
} else {
