}

markdownContent += `\n---\n\nMetadata:\n- Source: Obsidian ${dateInfo.filePath}\n- GeneratedAt: ${new Date().toISOString()}\n`;

// Base64 encode the markdown content
const markdownBase64 = Buffer.from(markdownContent).toString('base64');

return {
