// Calculate today's date in JST (UTC+9) for digest generation
// Digest is generated at 00:30 JST for today's journal
function getDigestDateJST() {
  const now = new Date();
  const jstOffset = 9 * 60; // JST = UTC+9
  const jstTime = new Date(now.getTime() + jstOffset * 60 * 1000);
  return jstTime.toISOString().split('T')[0]; // YYYY-MM-DD
}

const dateString = getDigestDateJST();

return {
  json: {
    date: dateString,
    timestamp: new Date().toISOString(),
    digestPath: `cortex/daily/${dateString}-digest.md`
  }
};

