// Prepare JSONL log entry for Recipe 14
const startTime = $('Every Night 00:30 JST').first().json.startTime || Date.now();
const endTime = Date.now();

// Get result data
const dateInfo = $('Calculate Digest Date (JST)').first().json;
const digestResult = $('Generate Daily Digest').first().json;

// Check which branch was taken (success or error)
let resultData;
try {
  resultData = $('Build Success Message').first().json;
} catch (e) {
  resultData = $('Build Error Message').first().json;
}

const isSuccess = resultData.success || false;

const logEntry = {
  ts: new Date().toISOString(),
  workflow: "Recipe 14: Daily Digest Generator",
  executionId: $executionId,
  status: isSuccess ? "success" : "error",
  durationMs: endTime - startTime,
  env: "production",
  errorMessage: isSuccess ? null : (resultData.error || digestResult.stderr || "Unknown error"),
  meta: {
    dateGenerated: dateInfo.date || resultData.date,
    digestPath: resultData.digestPath || dateInfo.digestPath || "unknown",
    digestLength: digestResult.stdout ? digestResult.stdout.length : 0,
    tasksExtracted: 0
  }
};

const today = dateInfo.date || new Date().toISOString().split('T')[0];
const filename = `cortex/logs/recipe-14-${today}.jsonl`;
const logLine = JSON.stringify(logEntry);

return {
  json: {
    logEntry: logEntry,
    logLine: logLine,
    filename: filename
  }
};
